const trackers=[],isOn=()=>{if(window.ga&&ga.create){return ga.getAll().forEach(e=>{""!==e.get("trackingId")&&trackers.push(e.get("name"))}),!0}return!1},sendEvent=(e,t,n,r)=>!!(e&&t&&n)&&(trackers.forEach(a=>{ga(`${a}.send`,{hitType:"event",eventCategory:e,eventAction:t,eventLabel:n||"",hitCallback:r||""})}),!0);export{isOn,sendEvent,trackers};